// React utilities for shared state
import { createContext, useState } from "react";

// Axios instance (API calls to Laravel)
import api from "../services/api";

// Create a context object
export const AuthContext = createContext();

// Provider component that wraps the app
export function AuthProvider({ children }) {

  // Store logged-in user info
  const [user, setUser] = useState(null);

  // Store auth token (from Laravel Sanctum)
  const [token, setToken] = useState(null);

  // Login function
  const login = async (email, password) => {
    // Call Laravel login API
    const res = await api.post("/login", { email, password });

    // Save returned user + token
    setUser(res.data.user);
    setToken(res.data.token);
  };

  // Register function
  const register = async (data) => {
    // Call Laravel register API
    const res = await api.post("/register", data);

    // Auto-login after registration
    setUser(res.data.user);
    setToken(res.data.token);
  };

  // Logout function
  const logout = () => {
    // Clear auth state
    setUser(null);
    setToken(null);
  };

  // Provide values to the rest of the app
  return (
    <AuthContext.Provider value={{
      user,
      token,
      login,
      register,
      logout
    }}>
      {children}
    </AuthContext.Provider>
  );
}
