import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import api from "../services/api";

export default function OwnerCourtSchedule() {
    const { courtId } = useParams();
    const [courtSchedule, setCourtSchedule] = useState([]);

    useEffect(() => {
        api.get(`/owner/courts/${courtId}/availability`)
            .then(res => setCourtSchedule(res.data));
        }, [courtId]
    );

    const addCourt = async () => {
        const res = await api.post("/courts", { name });
        setCourts([...courts, res.data]);
        setName("");
    };

    return (
        <div>
            <h2>Welcome to the court id number { courtId }</h2>
            <h1>Court { courtId } Schedules:</h1>

            <input
                value={name}
                onChange={e => setName(e.target.value)}
                placeholder="Court name"
            />
            <button onClick={addCourt}>Add Court</button>

        </div>
    );
}
